mapper.select:
select
from aipp_instance_log where instance_id in (
limit 5)

select

from aipp_instance_log

    true

        and aipp_id = #{aippId}


        and instance_id = #{instanceId}


        and create_at >= #{afterAt}


order by log_id

mapper.delete:
delete
from aipp_instance_log
where aipp_id = #{aippId}
and create_by = #{createUserAccount}
and aipp_type = #{aippType}

    and instance_id != #{instanceIdExclude}

delete
from aipp_instance_log
where aipp_id in

    #{id}

and create_by = #{createUserAccount}
and aipp_type = #{aippType}

    and instance_id != #{instanceIdExclude}

mapper.update:
update aipp_instance_log
set log_data = #{newLogData}::jsonb
where log_id = #{logId}

mapper.sql:
select instance_id from (select instance_id, create_at, row_number() over (partition by instance_id order by create_at desc) rn from aipp_instance_log where create_by = #{createUserAccount} and aipp_id in
#{id}
and aipp_type = #{aippType}) t where t.rn = 1 order by t.create_at desc

mapper.select:
select path from aipp_instance_log
instance_id = #{parentId}
LIMIT 1
