mapper.select:
SELECT 'total_requests' as metric, COUNT(*) AS value FROM conversation_record
WHERE app_id = #{appId} AND create_time BETWEEN #{startTime} AND #{endTime}
UNION
SELECT 'total_active_users' as metric, COUNT(DISTINCT user) AS value FROM conversation_record
WHERE app_id = #{appId} AND create_time BETWEEN #{startTime} AND #{endTime}
UNION
SELECT 'average_response_time' as metric, EXTRACT(EPOCH FROM AVG(finish_time - create_time))::bigint AS value FROM conversation_record
WHERE app_id = #{appId} AND create_time BETWEEN #{startTime} AND #{endTime}
